    private double calculate(String input) throws Exception {
        String[] tokens = input.split("(?<=\\D)|(?=\\D)");
        // 根据正则表达式将数字和运算符分离出来
        List<String> expression = new ArrayList<String>();
        for (int i = 0; i < tokens.length; i++) {
            if (!tokens[i].equals(" ")) {
                expression.add(tokens[i]);
            }
        }
        if (expression.size() % 2 == 0) {
            throw new Exception();
        }
        double result = Double.parseDouble(expression.get(0));
        for (int i = 1; i < expression.size(); i += 2) {
            String operator = expression.get(i);
            double operand = Double.parseDouble(expression.get(i + 1));
            switch (operator) {
                case "+":
                    result += operand;
                    break;
                case "-":
                    result -= operand;
                    break;
                case "*":
                    result *= operand;
                    break;
                case "/":
                    if (operand == 0) {
                        throw new Exception();
                    }
                    result /= operand;
                    break;
                default:
                    throw new Exception();
            }
        }
        return result;
    }
